import axios from 'axios'
import './Threads.css';
import { Link } from 'react-router-dom';
import { useState } from 'react';


export function Threads() {

    const [post, setPost] = useState([])


    function createThread(threadName) {
        console.log("create"+post)
        axios.post("https://2y6i6tqn41.execute-api.ap-northeast-1.amazonaws.com/threads", {
            "title": threadName
        })
            .then((response) => setPost(response.data.title))
            .then(() => console.log(post))

    }



    return (
        <div className='Content'>
            <h2>スレッド新規作成</h2>
            <form>
                <input type='text' placeholder='スレッド名' id="threadName"/>
            </form>
            <div className='Flex'>
                <Link to="/">Topに戻る</Link>
                <button type='button' onClick={() => createThread(document.getElementById("threadName").value)}>作成</button>
            </div>
        </div>
    )

}

export default Threads;
