import axios from "axios";
import "./Threads.css";
import { useEffect, useState } from "react";

export function ThreadsList() {

    const [threadsList, setThreadsList] = useState([])


    useEffect(() => {
        axios.get("https://2y6i6tqn41.execute-api.ap-northeast-1.amazonaws.com/threads?offset=0")
            .then((response) => {
                console.log(response)
                setThreadsList(response.data)

            })
    }, [])



    function showList() {
        return (
            threadsList.map(data => <tr><td className="threadNames">{data.title}</td></tr>)
        )
    }

    return (
        <div className="Content">
            <h2>新着スレッド</h2>
            <table className="threadName">
                <tbody>
                    {showList()}
                </tbody>
            </table>
        </div>

    )
}

export default ThreadsList;
